sp_angular_provider.script:
function spEventPubSub($rootScope) {
    var listenToEvent = "narrative.queryChanged";	

    function _publish(eventName, data) {
        $rootScope.$emit(eventName, data);
    }

    function _subscribe(eventName, callback) {
        $rootScope.$on(eventName, callback);
    }

		function notifyEventChange(data) {
			return _publish(listenToEvent, data);
		}

		function onEventChange(callback) {
			return  _subscribe(listenToEvent, callback);
		}

    return {
        notifyEventChange: notifyEventChange,
        onEventChange: onEventChange
    }


}
