sp_widget.client_script:
api.controller=function() {
  /* widget controller */
  var c = this;

    c.$onInit = function() {
			var colors = angular.copy(c.data.colors);

			c.colors = setColors(colors);
			c.nav = angular.copy(c.data.nav);
    };

	  c.guide = {};
	  c.guide.updated = '12/14/2020';
		c.colors = c.colors || [];
	  c.nav = c.nav || [];

		function setColors(colors) {
			var result = [];

			for (var i=0; i <colors.length; i++) {				
				result.push({
					name: colors[i].slice(7).replace('-',' '),
					css: colors[i].slice(7),
					code: colors[i]
				})
			}
			return result;

		}
};

