sp_widget.client_script:
api.controller=function(spUtil,$scope,$timeout) {
	/* widget controller */
	var c = this;
	$scope.hasError = false;
	$scope.error = {
		message: "", 
		url: ""
	}

	c.$onInit = function() {
		getNarrativeLoadDemo(narrSysId);
	};

	c.showPreviousBtn = false;
	c.scenariosListOptions = {
		"table": "x_snc_demohub_app_m2m_load_experie_narratives",
		"title": "Loaders",
		"filter": "",
		"display_field": "loader",
		"scenario_instance_description": "",
		"scenario_instance_thumbnail": "",
		"secondary_fields": "loader.description",
		"narrative_sys_id": "",
		"active": true,
		"always_show": true,
		"color": "default",
		"filter_acls": "false",
		"glyph": "empty",
		"maximum_entries": 9999,
		"order": 1,
		"order_by": "order",
		"order_direction": "asc",
		"panel_body_height": "30em",
		"size": "md",
		"hide_header": true,
		"scenario_executions":"",
		"executed_tailor": "",
		"loaders_executed": ""
	};

	var narrSysId = c.options.shared.sys_id;
	c.activeTab = c.options.shared.currentTarget.dataset.tab ? c.options.shared.currentTarget.dataset.tab : 'default';

	function getNarrativeLoadDemo(sysId) {
		c.isLoading = true;
		c.server.get({
			action: 'getNarrativeLoadDemo',
			sys_id: sysId
		}).then(function(response) {
			c.narrLoadDemo = response.data.narrative_load_demo;
			c.scenariosListOptions.scenario_instance_description = "";
			c.scenariosListOptions.scenario_instance_thumbnail = "";
			c.scenariosListOptions.loaders_executed = response.data.loaders_executed;
			c.scenariosListOptions.filter = response.data.loader_query;
			c.scenariosListOptions.narrative_sys_id = response.data.narrative_sys_id;
			spUtil.get("demohub_app_simple_list", c.scenariosListOptions).then(function(response) {
				c.scenarioListWidget = response;
				c.timeout();
				c.isLoading = false;
			});	
			c.isLoading = false;
		});		
	}

	c.previousState = function(){
		c.isLoading = true;
		c.showPreviousBtn = false;
		c.scenarioListWidget = {};

		spUtil.get("demohub_app_simple_list", c.scenariosListOptions).then(function(response) {
			c.scenarioListWidget = response;
			c.timeout();
			c.isLoading = false;
		});	
	};

	$scope.$on('loadExecutionCreated', function(event, data){
		if(data.load_execution_inserted){
			c.isLoading = true;
			c.showPreviousBtn = false;
			c.timeout();
			c.isLoading = false;
		}
	}); 

	$scope.$on('showPreviousBtn', function(event, showPrevious){
		c.timeout();
		c.showPreviousBtn = showPrevious;
	}); 

	$scope.$on('refreshStep4Data', function(event,data){
		c.$onInit();
	});

	$scope.$on('loaderFailed', function(event, data){
		if(data.display){
			$scope.hasError = true;
			$scope.error.message = data.message;
			$scope.error.url = data.url;
			c.$onInit();
		}else{
			$scope.hasError = false;
			$scope.error.message = data.message;
			$scope.error.url = data.url;
		}
	});

	c.timeout = function(){
		$timeout(function(){
			c.isLoading = false;
		}, 1500);
	};

};

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	if (input && input.action === 'getNarrativeLoadDemo') {
		data.narrative_sys_id = input.sys_id;
		data.narrative_load_demo = new x_snc_demohub_app.find().getStep4ModalInfo(data.narrative_sys_id);	
		data.loader_query = "narrative=" + data.narrative_sys_id;
		data.loaders_executed = {};

		data.narrative_load_demo.loader_array.forEach(function(loader){
			data.loaders_executed[loader.sys_id] = {executed: loader.executed, state: loader.state, date: loader.executed_time};
		});		
	}

})();
