sys_script_include.script:
var DHAppBookmarkUtil = Class.create();
DHAppBookmarkUtil.prototype = {
    BOOKMARK_TBL: 'sys_ui_bookmark',

    initialize: function() {},

    geFavBookmark: function(start, userId) {
        var result = [];
        var encodedQuery = 'urlSTARTSWITH/dhapp?^user=' + userId;

        var gr = new GlideRecord(this.BOOKMARK_TBL);
        gr.addEncodedQuery(encodedQuery);
        gr.query();

        while (gr.next()) {
            result.push(
                this._getFavBookmarkSysId(gr.getDisplayValue('url'), start)
            );
        }
        return result;
    },

    _getFavBookmarkSysId: function(url, start) {
		return url.slice(start);
    },

    type: 'DHAppBookmarkUtil'
};
